mapper.select:
select * from (select A.product_id as id, A.product_name as name, A.product_content as content, A.product_status_yn as statusYn, A.product_create_date as createDate, A.product_update_date as updateDate, A.product_delete_date as deleteDate, A.product_delete_yn as deleteYn, A.product_price as price, A.product_discount as discount, A.category_id as categoryId, A.parents_product_id as parentsId, (select product_image_name from product_image x where x.product_id = A.product_id order by x.product_image_id asc limit 1) AS imageName, C.product_category_id as category_id, C.product_category_name as category_name from product A INNER JOIN product_category C ON C.product_category_id = A.category_id WHERE 1=1
AND A.product_name LIKE CONCAT(#{keyword}, '%')
AND A.product_id = #{keyword}
AND (A.product_id = #{keyword} OR A.product_name LIKE CONCAT(#{keyword}, '%'))
order by A.product_id ) SA LIMIT #{pageSize} OFFSET #{offset}

mapper.delete:
delete from
    product_review_vote
where
    product_review_id = #{review.id}
  and user_id = #{user.id}
